mapper.update:
update board set title = '${board.title}', contents =
'${board.contents}'
where boardno = ${board.boardno}

mapper.select:
		select "rowno" from ( select rownum "rowno", t1.* from ( select boardno,
		LPAD('ㄴ',(LEVEL-1)*4,'→')||title title,contents,author,wdate from
		board
		
		
			

			
			
				title like #{search}
			
			
				contents like #{search}
			
			
				author like #{search}
			

		
		start with ref = 0 connect by prior boardno = ref order siblings by
		boardno ) t1 ) where boardno = #{board.boardno}

		select count(*) from board
		
			

			
			
				title like #{search}
			
			
				contents like #{search}
			
			
				author like #{search}

	
		select * from ( select rownum "rowno", t1.* from ( select boardno,
		LPAD('ㄴ',(LEVEL-1)*4,'→')||title title,contents,author,wdate from
		board
		
			

			
			
				title like #{search}
			
			
				contents like #{search}
			
			
				author like #{search}
			

		
		start with ref = 0 connect by prior boardno = ref order siblings by
		boardno ) t1 ) where "rowno" between (#{rpp}*#{page}-(#{rpp}-1)) and
		#{rpp}*#{page}
